$(".target").each((index, E) => {
    $(E).contents().each((index, elem) => {
        if (elem.nodeType == 3) {
            let text = elem.nodeValue;
            wrappedChar = [...text].map(char => `<span>${char}</span>`).join("");
            $(elem).replaceWith(wrappedChar);
        }
    });
    $(".target *").each((index, elem) => {
        $(elem).hide();
        setTimeout(() => {
            $(elem).show();
        }, 5 * index);
    })
});

